    def press_it(self,number):
        if self.label_display.text()=="ðŸ˜‚" + "delete me now":
            self.clear_display()
        # get the current value
        current = self.label_display.text()
        
        # if the current value is 0
        if current == "0":
            self.label_display.setText(number)
        else:
            
            self.label_display.setText(str(current) + str(number))


    def delete_btn_get(self):
        current=self.label_display.text()
        current=current[:-1]
        self.label_display.setText(current)
        
    # plus
    def plus_btn_get(self):
        if self.label_display.text()=="":
            return self.label_display.setText("0")
        get_number=self.label_display.text()
        global f_num 
        global math
        math="addition"
        f_num=float(get_number)
        self.clear_display()
        
