    def equls_fun_get(self):
        
        second_number=float(self.label_display.text())
        if math=="addition":
          
         
            self.label_display.setText(str(f_num + second_number))
            
        if math=="minus":
            self.clear_display()
      
            # total=str(f_num - second_number)
            self.label_display.setText(str(f_num - second_number))
            
        if math=="division":
            self.clear_display()
          
            # total=str(f_num - second_number)
            self.label_display.setText(str(f_num / second_number))
            
        if math=="multiplication":
            self.clear_display()
          
            # total=str(f_num - second_number)
            self.label_display.setText(str(f_num * second_number))
            
        if math == "percentage":
            
          self.clear_display()
       
          result = f_num * (second_number / 100)
          self.label_display.setText(str(result))
            
            
            
            
            
            
            
            
            
        
        
        
